mapper.update:
UPDATE S_gyulbbe.comment
SET comment_content = #{commentContent}, create_dtm = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
WHERE comment_seq = #{commentSeq}
AND member_id = #{memberId}

mapper.delete:
DELETE FROM S_gyulbbe.comment
WHERE comment_seq = #{commentSeq}
AND member_id = #{memberId}

mapper.select:
SELECT comment_content, create_dtm
FROM S_gyulbbe.comment
WHERE comment_seq = #{commentSeq}
